<?php if(!defined('BASEPATH')) exit('No direct script access allowed');Class MY_Controller extends Controller {	function MY_Controller() {		parent::Controller();	}		function LoadView($file, $data = '') {				$this->template['view'] = $file;		$this->load->vars($this->template);		$this->load->view('template/index', $data);	}		function LoadSingleView($file, $data = '') {		$this->template['view'] = $this->load->view($file, $data);		$this->load->vars($this->template);		$this->load->view('template/single');	}}